			if self.visited[i] == False:
				self.visited[i] = True
				Set = set(self.graph[i])
				Set.add(i)
				for j in self.graph[i]:
					Set1 = Set.copy()
					Set1.remove(j)
					self.visited[j] = True
					if set(self.graph[j]) != Set1:
