  <style>
    :root {
      --primary: #2c3e50;
      --secondary: #ecf0f1;
      --accent: #3498db;
      --light: #ffffff;
      --dark: #34495e;
      --spacing: 1rem;
    }
    * { box-sizing: border-box; margin: 0; padding: 0; }
