    }
    .container {
      display: grid;
      grid-template-areas:
        "header header"
        "sidebar main"
        "footer footer";
      grid-template-columns: 250px 1fr;
      grid-template-rows: auto 1fr auto;
      gap: var(--spacing);
      max-width: 1440px;
      margin: var(--spacing) auto;
    }
    header {
      grid-area: header;
      background: var(--light);
      padding: var(--spacing);
      display: flex;
      justify-content: space-between;
      align-items: center;
      border-bottom: 1px solid var(--dark);
