      background: var(--accent);
      color: var(--light);
    }
    main {
      grid-area: main;
      display: grid;
      grid-template-rows: auto auto auto auto;
      gap: var(--spacing);
    }
    .filters, .cards, .tables, .form-section, .nested-grid {
      background: var(--light);
      padding: var(--spacing);
      border-radius: 8px;
      box-shadow: 0 2px 5px rgba(0,0,0,0.1);
    }
    .filters {
      display: flex;
