    }
    .filters select, .filters input {
      margin-top: 0.25rem;
      padding: 0.5em;
      border: 1px solid var(--dark);
      border-radius: 4px;
    }
    .cards {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
      gap: var(--spacing);
    }
    .card {
      background: var(--secondary);
      padding: var(--spacing);
      border-radius: 8px;
      text-align: center;
    }
    .tables table {
      width: 100%;
      border-collapse: collapse;
      margin-bottom: var(--spacing);
    }
    .tables th, .tables td {
      border: 1px solid var(--dark);
      padding: 0.5em;
      text-align: left;
    }
    .tables th {
      background: var(--accent);
      color: var(--light);
      position: sticky;
      top: 0;
    }
    .tables tr:nth-child(even) {
      background: var(--secondary);
    }
    .form-section form {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: var(--spacing);
    }
    .form-section fieldset {
      border: 1px solid var(--dark);
      border-radius: 4px;
      padding: var(--spacing);
    }
    .form-section legend {
      font-weight: bold;
