    }
    .form-section label {
      display: flex;
      align-items: center;
      gap: 0.5em;
      margin-bottom: var(--spacing);
    }
    .nested-grid {
      display: grid;
      grid-template-columns: 2fr 1fr;
      gap: var(--spacing);
    }
    .nested-grid .sub-grid {
      display: grid;
      grid-template-columns: 1fr 1fr;
      grid-template-rows: 1fr 1fr;
      gap: var(--spacing);
    }
    footer {
      grid-area: footer;
