    * { box-sizing: border-box; margin: 0; padding: 0; }
    body {
      font-family: var(--font);
      background: var(--light-bg);
      color: var(--dark-text);
      line-height: 1.6;
    }
    header, footer {
      background: var(--primary);
      color: #fff;
      padding: var(--gap);
      text-align: center;
    }
    nav ul {
      list-style: none;
      display: flex;
      gap: var(--gap);
      justify-content: center;
    }
    nav li {
      position: relative;
    }
    nav li:hover > ul {
      display: block;
    }
    nav a {
      color: #fff;
      text-decoration: none;
      padding: 0.5rem;
      display: block;
    }
    nav ul ul {
      position: absolute;
      top: 100%;
      left: 0;
      background: var(--primary);
      display: none;
      flex-direction: column;
      min-width: 150px;
    }
    .container {
      display: grid;
      grid-template-areas:
        "sidebar main"
        "sidebar footer";
      grid-template-columns: 250px 1fr;
      grid-template-rows: auto 60px;
      gap: var(--gap);
      padding: var(--gap);
    }
    aside.sidebar {
