def solve():
    n = int(input())
    a = list(map(int, input().split()))

    if n == 1:
        if a[0] > 0:
            print("BitLGM")
        else:
            print("BitAryo")
        return

    def can_move(arr):
        for val in arr:
            if val > 0:
                return True
        return False

    def get_winner(arr, turn):
        if not can_move(arr):
            if turn == 0:
                return "BitAryo"
            else:
                return "BitLGM"

        winners = []

        # Move 1: Take one integer and decrease it
        for i in range(len(arr)):
            if arr[i] > 0:
                for x in range(1, arr[i] + 1):
                    new_arr = arr[:]
                    new_arr[i] -= x
                    winners.append(get_winner(new_arr, 1 - turn))

        # Move 2: Decrease all integers by x
        min_val = min(arr)
        if min_val > 0:
            for x in range(1, min_val + 1):
                new_arr = [(val - x) for val in arr]
                winners.append(get_winner(new_arr, 1 - turn))

        if turn == 0: # BitLGM's turn, looking for a win
            if "BitLGM" in winners:
                return "BitLGM"
            else:
                return "BitAryo"
        else: # BitAryo's turn, looking for a win
            if "BitAryo" in winners:
                return "BitAryo"
            else:
                return "BitLGM"

    print(get_winner(a, 0))

