def find_path(n, edges, start, end):

    adj = [[] for _ in range(n + 1)]
    for i, (x, y, _) in enumerate(edges):
        adj[x].append((y, i))
        adj[y].append((x, i))

    queue = [(start, [])]
    visited = {start}

    while queue:
        node, path = queue.pop(0)

        if node == end:
            return path

        for neighbor, edge_index in adj[node]:
            if neighbor not in visited:
                visited.add(neighbor)
                queue.append((neighbor, path + [edge_index]))

    return None

