def solve():
    n, k, A = map(int, input().split())
    points = []
    for _ in range(n):
        points.append(list(map(int, input().split())))

    min_cost = float('inf')

    for i in range(1 << n):
        current_cost = 0
        remaining_points = []

        # Calculate cost of erasing individual points
        for j in range(n):
            if (i >> j) & 1:
                current_cost += points[j][2]
            else:
                remaining_points.append(points[j])

        # If all points are erased individually, update min_cost and continue
        if not remaining_points:
            min_cost = min(min_cost, current_cost)
            continue

        # Try to erase remaining points using triangles
        triangle_cost = 0

        # Iterate through all possible triangles
        for a in range(k):
            for b in range(k - a):
                triangle_points = []
                for point in remaining_points:
                    x, y, _ = point
                    if x >= a and y >= b and x + y < k:
                        triangle_points.append(point)

                # If triangle contains points, calculate cost and remove points
                if triangle_points:
                    triangle_cost = (k - a - b) * A

                    # Create a copy of remaining points
                    temp_remaining_points = remaining_points[:]

                    # Remove points covered by triangle
                    for point in triangle_points:
                        temp_remaining_points.remove(point)

                    # Calculate cost of erasing remaining points individually
                    individual_cost = 0
                    for point in temp_remaining_points:
                        individual_cost += point[2]

                    # Update min_cost
                    min_cost = min(min_cost, current_cost + triangle_cost + individual_cost)

        # If no triangle can cover any points, calculate cost of erasing all points individually
        if triangle_cost == 0:
            individual_cost = 0
            for point in remaining_points:
                individual_cost += point[2]
            min_cost = min(min_cost, current_cost + individual_cost)

