class LazySegTree():
    """
        V:  初始序列，树叶节点
        OP: 节点/线段 之间的合并操作
        E:  节点/线段 幺元。op(e, x) = op(x, e) = x
        Mapping:        对线段进行F操作
        COMPOSITION:    复合F与G：返回F(G(seg))
        ID:             恒等映射：F(ID(seg)) = F(seg)
    """
    __slots__ = ['n', 'log', 'size', 'd', 'lz', 'e', 'op', 'mapping', 'composition', 'identity']
    def _update(self, k):self.d[k]=self.op(self.d[2 * k], self.d[2 * k + 1])
    def _all_apply(self, k, f):
        self.d[k]=self.mapping(f,self.d[k])
        if (k<self.size):self.lz[k]=self.composition(f,self.lz[k])
    def _push(self, k):
        self._all_apply(2 * k, self.lz[k])
        self._all_apply(2 * k + 1, self.lz[k])
        self.lz[k]=self.identity
    def __init__(self,V,OP,E,MAPPING,COMPOSITION,ID):
        self.n=len(V)
        self.log=(self.n-1).bit_length()
        self.size=1<<self.log
        self.d=[E for i in range(2*self.size)]
        self.lz=[ID for i in range(self.size)]
        self.e=E
        self.op=OP
        self.mapping=MAPPING
        self.composition=COMPOSITION
        self.identity=ID
        for i in range(self.n):self.d[self.size+i]=V[i]
        for i in range(self.size-1,0,-1):self._update(i)
    def set(self,p,x):
        assert 0<=p and p<self.n
        p+=self.size
        for i in range(self.log,0,-1):self._push(p >> i)
        self.d[p]=x
        for i in range(1,self.log+1):self._update(p >> i)
    def get(self,p):
        assert 0<=p and p<self.n
        p+=self.size
        for i in range(self.log,0,-1):self._push(p >> i)
        return self.d[p]
    def prod(self,l,r):
        assert 0<=l and l<=r and r<=self.n
        if l==r:return self.e
        l+=self.size
        r+=self.size
        for i in range(self.log,0,-1):
            if (((l>>i)<<i)!=l):self._push(l >> i)
            if (((r>>i)<<i)!=r):self._push(r >> i)
        sml,smr=self.e,self.e
        while(l<r):
            if l&1:
                sml=self.op(sml,self.d[l])
                l+=1
            if r&1:
                r-=1
                smr=self.op(self.d[r],smr)
            l>>=1
            r>>=1
        return self.op(sml,smr)
    def all_prod(self):return self.d[1]
    def apply_point(self,p,f):
        assert 0<=p and p<self.n
        p+=self.size
        for i in range(self.log,0,-1):self._push(p >> i)
        self.d[p]=self.mapping(f,self.d[p])
        for i in range(1,self.log+1):self._update(p >> i)
    def apply(self,l,r,f):
        assert 0<=l and l<=r and r<=self.n
        if l==r:return
        l+=self.size
        r+=self.size
        for i in range(self.log,0,-1):
            if (((l>>i)<<i)!=l):self._push(l >> i)
            if (((r>>i)<<i)!=r):self._push((r - 1) >> i)
        l2,r2=l,r
        while(l<r):
            if (l&1):
                self._all_apply(l, f)
                l+=1
            if (r&1):
                r-=1
                self._all_apply(r, f)
            l>>=1
            r>>=1
        l,r=l2,r2
        for i in range(1,self.log+1):
            if (((l>>i)<<i)!=l):self._update(l >> i)
            if (((r>>i)<<i)!=r):self._update((r - 1) >> i)
    def max_right(self,l,g):
        assert 0<=l and l<=self.n
        assert g(self.e)
        if l==self.n:return self.n
        l+=self.size
        for i in range(self.log,0,-1):self._push(l >> i)
        sm=self.e
        while(1):
